import React, { useState, useEffect } from "react";
import Signup from "./components/Signup";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Dashboard from "./components/Dashboard";
import Appliance from "./components/Appliance";
import Profile from "./components/Profile";

function App() {
  const [page, setPage] = useState("login");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedPage = localStorage.getItem("currentPage") || "login";

    if (token) {
      setIsAuthenticated(true);
      // Prevent showing auth pages if already logged in
      if (["login", "signup", "forgot"].includes(savedPage)) {
        setPage("dashboard");
      } else {
        setPage(savedPage);
      }
    } else {
      // No token = force to login
      setIsAuthenticated(false);
      setPage("login");
    }
  }, []);

  // Save current page in localStorage for refresh persistence
  const handleSwitch = (newPage) => {
    setPage(newPage);
    localStorage.setItem("currentPage", newPage);
  };

  //Logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("currentPage");
    setIsAuthenticated(false);
    setPage("login");
  };

  return (
    <div className="text-center min-h-screen">
      {!isAuthenticated ? (
        <>
          {page === "signup" && <Signup onSwitch={handleSwitch} />}
          {page === "login" && (
            <Login
              onSwitch={(newPage) => {
                if (newPage === "dashboard") setIsAuthenticated(true);
                handleSwitch(newPage);
              }}
            />
          )}
          {page === "forgot" && <ForgotPassword onSwitch={handleSwitch} />}
        </>
      ) : (
        <>
          {page === "dashboard" && (
            <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "appliance" && (
            <Appliance onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "profile" && (
            <Profile onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
