import React, { useState, useEffect } from "react";
import Signup from "./components/Signup";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Dashboard from "./components/Dashboard";
import Appliance from "./components/Appliance";
import Profile from "./components/Profile";

function App() {
  const [page, setPage] = useState("login");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isCheckingAuth, setIsCheckingAuth] = useState(true);
  const [hasInitialized, setHasInitialized] = useState(false);

 useEffect(() => {
  const checkAuth = async () => {
    console.log("ðŸ”„ Checking authentication...");
    const token = localStorage.getItem("token");
    const savedPage = localStorage.getItem("currentPage");
    
    if (!token) {
      console.log("No token found â†’ go to login");
      setIsAuthenticated(false);
      setPage("login");
      setIsCheckingAuth(false);
      return;
    }

    try {
      // OPTIONAL: verify token via backend
      const res = await fetch("http://localhost:5000/api/users/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) throw new Error("Invalid token");

      // token is valid
      setIsAuthenticated(true);
      if (["dashboard", "appliance", "profile"].includes(savedPage)) {
        setPage(savedPage);
      } else {
        setPage("dashboard");
      }
    } catch (err) {
      console.log("âŒ Invalid token â†’ clearing storage", err.message);
      localStorage.removeItem("token");
      localStorage.removeItem("currentPage");
      setIsAuthenticated(false);
      setPage("login");
    } finally {
      setIsCheckingAuth(false);
      setHasInitialized(true);
    }
  };

  checkAuth();
}, []);

  const handleSwitch = (newPage) => {
    console.log("ðŸ“„ Switching to page:", newPage);
    setPage(newPage);

    // Only save protected routes (dashboard, appliance, profile)
    // Never save auth pages (login, signup, forgot)
    if (["dashboard", "appliance", "profile"].includes(newPage)) {
      localStorage.setItem("currentPage", newPage);
    } else {
      // Clear saved page when on auth pages
      localStorage.removeItem("currentPage");
    }
  };

  const handleLogout = () => {
    console.log("ðŸšª Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("currentPage");
    setIsAuthenticated(false);
    setPage("login");
  };

  if (isCheckingAuth || !hasInitialized) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-white text-gray-500">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="text-center min-h-screen">
      {page === "signup" || page === "login" || page === "forgot" ? (
        <>
          {page === "signup" && <Signup onSwitch={handleSwitch} />}
          {page === "login" && <Login onSwitch={handleSwitch} />}
          {page === "forgot" && <ForgotPassword onSwitch={handleSwitch} />}
        </>
      ) : (
        <>
          {page === "dashboard" && (
            <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "appliance" && (
            <Appliance onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "profile" && (
            <Profile onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
