import React, { useEffect, useState } from "react";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell, BarChart, Bar, ResponsiveContainer
} from "recharts";

// Lightweight shadcn-style card components
const Card = ({ children, className = "" }) => (
  <div className={`bg-white rounded-xl shadow-xl overflow-hidden ${className}`}>{children}</div>
);
const CardHeader = ({ title }) => (
  <div className="bg-blue-500 text-white px-4 py-2 font-semibold rounded-t-xl">{title}</div>
);
const CardContent = ({ children }) => (
  <div className="p-4 bg-white flex items-center justify-center">{children}</div>
);

// Sidebar icon wrapper
const LeftIcon = ({ children, onClick }) => (
  <div
    onClick={onClick}
    className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60"
  >
    {children}
  </div>
);

const Dashboard = ({ onSwitch, token, homeId }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [error, setError] = useState(null);
  const bgUrl = "https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=2000&q=80";

  const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  const [overallData, setOverallData] = useState([]);
  const [roomData, setRoomData] = useState([]);
  const [applianceData, setApplianceData] = useState([]);
  const [historyData, setHistoryData] = useState([]);
  const [overallBill, setOverallBill] = useState(0);
  const [upcomingBill, setUpcomingBill] = useState(0);

  useEffect(() => {
    if (token) {
      loadDashboardData();
      const interval = setInterval(loadDashboardData, 10000);
      return () => clearInterval(interval);
    }
  }, [token, homeId]);

  const loadDashboardData = async () => {
    try {
      setError(null);
      const params = new URLSearchParams();
      if (homeId) params.append('homeId', homeId);

      const appliancesResponse = await fetch(`${API_BASE_URL}/appliances?${params}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!appliancesResponse.ok) throw new Error('Failed to fetch appliances');
      const appliances = await appliancesResponse.json();

      const energyResponse = await fetch(`${API_BASE_URL}/energy?${params}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!energyResponse.ok) throw new Error('Failed to fetch energy readings');
      const energyReadings = await energyResponse.json();

      let summaries = [];
      if (homeId) {
        const summaryResponse = await fetch(`${API_BASE_URL}/energy/summary/${homeId}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (summaryResponse.ok) {
          summaries = await summaryResponse.json();
        }
      }

      processEnergyReadings(energyReadings, appliances);
      processSummaries(summaries);
    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError(err.message);
      loadDefaultData();
    }
  };

  const processEnergyReadings = (readings, appliances) => {
    if (!readings || readings.length === 0) {
      loadDefaultData();
      return;
    }

    const applianceMap = {};
    appliances.forEach(app => {
      applianceMap[app._id] = app;
    });

    const roomConsumption = {};
    const applianceConsumption = {};

    readings.forEach(reading => {
      const energyKwh = reading.energy || 0;
      const roomName = reading.roomId?.name || 'Other';
      roomConsumption[roomName] = (roomConsumption[roomName] || 0) + energyKwh;

      const applianceName = reading.applianceId?.name || 'Unknown';
      applianceConsumption[applianceName] = (applianceConsumption[applianceName] || 0) + energyKwh;
    });

    const roomDataArray = Object.entries(roomConsumption)
      .map(([name, value]) => ({ name, value: parseFloat(value.toFixed(2)) }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 6);

    const applianceDataArray = Object.entries(applianceConsumption)
      .map(([name, value]) => ({ name, value: parseFloat(value.toFixed(2)) }))
      .sort((a, b) => b.value - a.value)
      .slice(0, 6);

    setRoomData(roomDataArray.length > 0 ? roomDataArray : getDefaultRoomData());
    setApplianceData(applianceDataArray.length > 0 ? applianceDataArray : getDefaultApplianceData());

    const sortedReadings = [...readings]
      .sort((a, b) => new Date(b.recorded_at) - new Date(a.recorded_at))
      .slice(0, 10);

    const formattedHistory = sortedReadings.map((reading, idx) => {
      const prevReading = readings[idx + 1];
      const currentKwh = reading.energy || 0;
      const pastKwh = prevReading?.energy || 0;
      const percentage = pastKwh > 0 ? (((currentKwh - pastKwh) / pastKwh) * 100).toFixed(1) : 'N/A';
      return {
        location: reading.roomId?.name || reading.applianceId?.name || 'Unknown',
        timestamp: new Date(reading.recorded_at).toLocaleString(),
        currentKwh: `${currentKwh.toFixed(2)} kWh`,
        pastKwh: `${pastKwh.toFixed(2)} kWh`,
        percentage: percentage !== 'N/A' ? `${percentage}%` : 'N/A'
      };
    });

    setHistoryData(formattedHistory);

    const totalCost = readings.reduce((sum, r) => sum + (r.cost || 0), 0);
    const recentCost = readings
      .filter(r => {
        const readingDate = new Date(r.recorded_at);
        const thirtyDaysAgo = new Date();
        thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
        return readingDate >= thirtyDaysAgo;
      })
      .reduce((sum, r) => sum + (r.cost || 0), 0);

    setOverallBill(Math.round(totalCost));
    setUpcomingBill(Math.round(recentCost));
  };

  const processSummaries = (summaries) => {
    if (!summaries || summaries.length === 0) {
      setOverallData(getDefaultOverallData());
      return;
    }

    const periodMap = {};
    summaries.forEach(summary => {
      const period = summary.period || 'Unknown';
      if (!periodMap[period]) {
        periodMap[period] = { month: period, production: 0, consumption: 0 };
      }
      periodMap[period].consumption += summary.total_energy || 0;
    });

    const chartData = Object.values(periodMap).sort((a, b) => a.month.localeCompare(b.month));
    setOverallData(chartData.length > 0 ? chartData : getDefaultOverallData());
  };

  const loadDefaultData = () => {
    setOverallData(getDefaultOverallData());
    setRoomData(getDefaultRoomData());
    setApplianceData(getDefaultApplianceData());
    setHistoryData([]);
    setOverallBill(1000000);
    setUpcomingBill(123467);
  };

  const getDefaultOverallData = () => [
    { month: "1997", production: 150000, consumption: 150000 },
    { month: "1998", production: 180000, consumption: 170000 },
    { month: "1999", production: 200000, consumption: 190000 },
  ];

  const getDefaultRoomData = () => [
    { name: "Master Bedroom", value: 40 },
    { name: "Living Room", value: 30 },
    { name: "Kitchen", value: 5 },
  ];

  const getDefaultApplianceData = () => [
    { name: "Lights", value: 150 },
    { name: "Computer", value: 80 },
    { name: "Refrigerator", value: 130 },
  ];

  const COLORS = ["#4CAF50", "#FF9800", "#2196F3", "#FFEB3B", "#9C27B0", "#607D86"];

  return (
    <div className="h-screen w-screen overflow-hidden relative font-sans">
      <div className="absolute inset-0 bg-cover bg-center" style={{ backgroundImage: `url(${bgUrl})` }} />
      <div className="absolute inset-0 bg-black opacity-60 backdrop-blur-sm" />

      {/* Sidebar */}
      <aside className="absolute left-6 top-1/3 transform -translate-y-1/3 z-20">
        <div className="flex flex-col items-center">
          <div className="bg-white bg-opacity-6 p-3 rounded-lg shadow-lg flex flex-col items-center">
            <LeftIcon onClick={() => onSwitch("dashboard")}>üè†</LeftIcon>
            <LeftIcon onClick={() => onSwitch("appliance")}>‚öôÔ∏è</LeftIcon>
            <LeftIcon onClick={() => onSwitch("profile")}>üë§</LeftIcon>
            <LeftIcon onClick={() => setShowLogoutConfirm(true)}>üö™</LeftIcon>
          </div>
        </div>
      </aside>

      {/* Logout modal */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm mx-4 w-full">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Confirm Logout</h3>
            <p className="text-gray-600 mb-6 text-center">Are you sure you want to logout?</p>
            <div className="flex justify-center items-center gap-4">
              <button
                onClick={() => setShowLogoutConfirm(false)}
                className="px-6 py-2 text-gray-600 hover:text-gray-800 rounded border border-gray-300 hover:border-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={() => onSwitch("login")}
                className="px-6 py-2 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Error message */}
      {error && (
        <div className="absolute top-4 right-4 bg-red-500 text-white px-6 py-3 rounded-lg shadow-lg z-50 max-w-md">
          <p className="font-semibold">Error loading data</p>
          <p className="text-sm">{error}</p>
        </div>
      )}

      {/* Main content */}
      <div className="relative z-10 h-full w-full flex items-start justify-center overflow-y-auto">
        <div className="w-[94%] max-w-[1200px] mt-6 pb-6">
          <h1 className="text-3xl tracking-wider font-bold text-white text-center mb-6">DASHBOARD</h1>

          <div className="grid grid-cols-2 gap-6 mb-6">
            {/* Left column */}
            <div className="flex flex-col gap-6">
              <Card className="flex-1">
                <CardHeader title="OVERALL CONSUMPTION" />
                <CardContent>
                  <ResponsiveContainer width="100%" height={180}>
                    <LineChart data={overallData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" style={{ fontSize: '10px' }} />
                      <YAxis style={{ fontSize: '10px' }} />
                      <Tooltip />
                      <Legend wrapperStyle={{ fontSize: '11px' }} />
                      <Line type="monotone" dataKey="production" stroke="#2196F3" strokeWidth={2} />
                      <Line type="monotone" dataKey="consumption" stroke="#FF5252" strokeWidth={2} />
                    </LineChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>

              <div className="grid grid-cols-2 gap-6">
                <div className="bg-white rounded-lg shadow-xl p-4">
                  <div className="text-sm text-gray-600">Overall Bill</div>
                  <div className="text-2xl font-bold mt-4">‚Ç±{overallBill.toLocaleString()}</div>
                </div>
                <div className="bg-white rounded-lg shadow-xl p-4">
                  <div className="text-sm text-gray-600">Upcoming Bill</div>
                  <div className="text-2xl font-bold mt-4">‚Ç±{upcomingBill.toLocaleString()}</div>
                </div>
              </div>
            </div>

            {/* Right column */}
            <div className="flex flex-col gap-6">
              <Card className="flex-1">
                <CardHeader title="CONSUMPTION BY ROOM" />
                <CardContent>
                  <ResponsiveContainer width="100%" height={180}>
                    <PieChart>
                      <Pie
                        data={roomData}
                        cx="50%"
                        cy="50%"
                        outerRadius={70}
                        dataKey="value"
                        label={({ name, value }) => `${name}: ${value} kWh`}
                        style={{ fontSize: '10px' }}
                      >
                        {roomData.map((_, i) => (
                          <Cell key={i} fill={COLORS[i % COLORS.length]} />
                        ))}
                      </Pie>
                      <Tooltip />
                    </PieChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>

              <Card className="flex-1">
                <CardHeader title="APPLIANCES" />
                <CardContent>
                  <ResponsiveContainer width="100%" height={160}>
                    <BarChart data={applianceData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" style={{ fontSize: '10px' }} />
                      <YAxis style={{ fontSize: '10px' }} />
                      <Tooltip />
                      <Bar dataKey="value">
                        {applianceData.map((_, i) => (
                          <Cell key={i} fill={COLORS[i % COLORS.length]} />
                        ))}
                      </Bar>
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </div>
          </div>

          {/* History */}
          <div className="bg-white rounded-xl shadow-xl overflow-hidden">
            <div className="bg-blue-500 text-white px-4 py-2 font-semibold rounded-t-xl">HISTORY</div>
            <div className="p-4 h-40 overflow-auto">
              {historyData.length > 0 ? (
                <table className="w-full text-left border-collapse">
                  <thead>
                    <tr className="text-xs text-gray-500">
                      <th className="pb-2">LOCATION</th>
                      <th className="pb-2">TIMESTAMP</th>
                      <th className="pb-2">CURRENT kWh</th>
                      <th className="pb-2">PAST kWh</th>
                      <th className="pb-2">PERCENTAGE</th>
                    </tr>
                  </thead>
                  <tbody>
                    {historyData.map((row, idx) => (
                      <tr key={idx} className="border-t">
                        <td className="py-3">{row.location}</td>
                        <td className="py-3">{row.timestamp}</td>
                        <td className="py-3">{row.currentKwh}</td>
                        <td className="py-3">{row.pastKwh}</td>
                        <td className="py-3">{row.percentage}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center text-gray-500 py-8">
                  No history data available
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
