import React, { useState, useEffect } from 'react';
import dashboardIcon from '../assets/icons/dashboard.svg';
import applianceIcon from '../assets/icons/add.svg';
import profileIcon from '../assets/icons/profile.svg';
import logoutIcon from '../assets/icons/logout.svg';

const NavItem = ({ onClick, src, label }) => (
  <div
    onClick={onClick}
    className="w-12 h-12 flex items-center justify-center rounded-lg mb-3 bg-blue-600 text-white cursor-pointer shadow-md hover:bg-blue-700 hover:shadow-lg transition-all duration-200"
    title={label}
  >
    <img src={src} alt={label} className="w-6 h-6" />
  </div>
);

const ApplianceCard = ({ name, image, onDelete }) => (
  <div className="group relative bg-white/90 backdrop-blur-sm rounded-xl p-5 shadow-md hover:shadow-xl transition-all duration-300 cursor-pointer border border-gray-100">
    <button
      onClick={(e) => {
        e.stopPropagation();
        onDelete();
      }}
      className="absolute top-2 right-2 w-7 h-7 bg-red-500 text-white rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity text-sm font-bold"
    >
      X
    </button>
    <div className="w-16 h-16 mx-auto mb-3 flex items-center justify-center">
      <img src={image} alt={name} className="w-full h-full object-contain" />
    </div>
    <p className="text-center text-gray-700 font-medium text-sm">{name}</p>
  </div>
);

const AddApplianceCard = ({ onClick }) => (
  <div
    onClick={onClick}
    className="bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-xl p-5 shadow-md hover:shadow-xl transition-all duration-300 cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-white/50"
  >
    <svg className="w-10 h-10 mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
    </svg>
    <p className="text-sm font-medium">Add Appliance</p>
  </div>
);

const AddHomeCard = ({ onClick }) => (
  <div
    onClick={onClick}
    className="bg-gradient-to-br from-green-500 to-green-600 text-white rounded-xl p-5 shadow-md hover:shadow-xl transition-all duration-300 cursor-pointer flex flex-col items-center justify-center border-2 border-dashed border-white/50"
  >
    <svg className="w-10 h-10 mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
    </svg>
    <p className="text-sm font-medium">Add New Home</p>
  </div>
);

const HomeCard = ({ home, onSelect, onDelete }) => (
  <div
    onClick={onSelect}
    className="group relative bg-white/90 backdrop-blur-sm rounded-xl p-5 shadow-md hover:shadow-xl transition-all duration-300 cursor-pointer border border-gray-100"
  >
    <button
      onClick={(e) => {
        e.stopPropagation();
        onDelete();
      }}
      className="absolute top-2 right-2 w-7 h-7 bg-red-500 text-white rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity text-sm font-bold"
    >
      X
    </button>
    <div className="w-16 h-16 mx-auto mb-3 bg-blue-100 rounded-full flex items-center justify-center">
      <svg className="w-9 h-9 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
      </svg>
    </div>
    <p className="text-center text-gray-800 font-semibold text-base">{home.name}</p>
    <p className="text-center text-xs text-gray-500 mt-1">{home.appliances.length} appliance{home.appliances.length !== 1 ? 's' : ''}</p>
  </div>
);

const Appliance = ({ onSwitch }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [entryType, setEntryType] = useState('manual');
  const [selectedAppliance, setSelectedAppliance] = useState('');
  const [selectedRoom, setSelectedRoom] = useState('');
  const [energy, setEnergy] = useState('');
  const [homes, setHomes] = useState([]);
  const [showAddHomeModal, setShowAddHomeModal] = useState(false);
  const [newHomeName, setNewHomeName] = useState('');
  const [selectedHomeIndex, setSelectedHomeIndex] = useState(null);
  const [showDeleteHomeConfirm, setShowDeleteHomeConfirm] = useState(false);
  const [homeToDelete, setHomeToDelete] = useState(null);
  const [showDeleteApplianceConfirm, setShowDeleteApplianceConfirm] = useState(false);
  const [applianceToDelete, setApplianceToDelete] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [rooms, setRooms] = useState([]);

  const defaultAppliances = [
    { id: 'default-fridge', name: 'Fridge', image: 'data:image/svg+xml,%3Csvg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"%3E%3Cpath d="M4 6a2 2 0 012-2h12a2 2 0 012 2v12a2 2 0 01-2 2H6a2 2 0 01-2-2V6z" stroke="currentColor" stroke-width="2"/%3E%3Cpath d="M6 9h12M6 15h12" stroke="currentColor" stroke-width="2"/%3E%3C/svg%3E', defaultEnergy: 1.5 },
    { id: 'default-television', name: 'Television', image: 'data:image/svg+xml,%3Csvg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"%3E%3Cpath d="M4 6a2 2 0 012-2h12a2 2 0 012 2v8a2 2 0 01-2 2H6a2 2 0 01-2-2V6z" stroke="currentColor" stroke-width="2"/%3E%3Cpath d="M8 18h8" stroke="currentColor" stroke-width="2"/%3E%3C/svg%3E', defaultEnergy: 0.5 },
    { id: 'default-ac', name: 'Air Conditioner', image: 'data:image/svg+xml,%3Csvg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"%3E%3Cpath d="M4 6a2 2 0 012-2h12a2 2 0 012 2v4a2 2 0 01-2 2H6a2 2 0 01-2-2V6z" stroke="currentColor" stroke-width="2"/%3E%3Cpath d="M8 16l4 4m4-4l-4 4" stroke="currentColor" stroke-width="2"/%3E%3C/svg%3E', defaultEnergy: 3.0 },
    { id: 'default-lights', name: 'Lights', image: 'data:image/svg+xml,%3Csvg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"%3E%3Cpath d="M12 18V6m0 0h4a2 2 0 012 2v4a2 2 0 01-2 2h-4V6zM8 6h4m0 0h-4a2 2 0 00-2 2v4a2 2 0 002 2h4" stroke="currentColor" stroke-width="2"/%3E%3C/svg%3E', defaultEnergy: 0.1 },
    { id: 'default-speaker', name: 'Speaker', image: 'data:image/svg+xml,%3Csvg width="64" height="64" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"%3E%3Cpath d="M12 8a4 4 0 100 8 4 4 0 000-8z" stroke="currentColor" stroke-width="2"/%3E%3Cpath d="M4 6a2 2 0 012-2h12a2 2 0 012 2v12a2 2 0 01-2 2H6a2 2 0 01-2-2V6z" stroke="currentColor" stroke-width="2"/%3E%3C/svg%3E', defaultEnergy: 0.2 },
  ];

  const API_BASE = 'http://localhost:5000/api';

  const loadAppliancesForHome = async (homeId, index) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`${API_BASE}/appliances?homeId=${homeId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('Failed to fetch appliances');
      const data = await res.json();
      setHomes((prev) => {
        const copy = [...prev];
        copy[index] = {
          ...copy[index],
          appliances: data.map((a) => ({
            id: a._id || a.id,
            name: a.name,
            type: a.type,
            image: defaultAppliances.find((da) => da.name === a.type)?.image || '',
          })),
        };
        return copy;
      });
    } catch (err) {
      console.error('Load appliances error', err.message || err);
    }
  };

  useEffect(() => {
    const fetchHomesAndAppliances = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;
      try {
        const res = await fetch(`${API_BASE}/homes`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error('Failed to fetch homes');
        const data = await res.json();
        const normalized = data.map((h) => ({
          id: h._id || h.id,
          name: h.name,
          appliances: [],
        }));
        setHomes(normalized);
        for (let i = 0; i < normalized.length; i++) {
          await loadAppliancesForHome(normalized[i].id, i);
        }
        const savedIndex = localStorage.getItem('selectedHomeIndex');
        if (savedIndex !== null && Number(savedIndex) < normalized.length) {
          setSelectedHomeIndex(Number(savedIndex));
        }
      } catch (error) {
        console.error('Error fetching homes:', error);
      }
    };
    fetchHomesAndAppliances();
  }, []);

  useEffect(() => {
    if (selectedHomeIndex !== null) {
      localStorage.setItem('selectedHomeIndex', selectedHomeIndex);
    } else {
      localStorage.removeItem('selectedHomeIndex');
    }
  }, [selectedHomeIndex]);

  useEffect(() => {
    const fetchRooms = async () => {
      if (selectedHomeIndex === null || !homes[selectedHomeIndex]) {
        setRooms([]);
        return;
      }
      const token = localStorage.getItem('token');
      if (!token) return;
      const homeId = homes[selectedHomeIndex].id;
      try {
        const res = await fetch(`${API_BASE}/rooms?homeId=${homeId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error('Failed to fetch rooms');
        const data = await res.json();
        setRooms(data);
      } catch (err) {
        console.error('Fetch rooms error:', err);
        setRooms([]);
      }
    };
    fetchRooms();
  }, [selectedHomeIndex, homes]);

  const handleSelectHome = (index) => setSelectedHomeIndex(index);
  const handleBackToHomes = () => {
    setSelectedHomeIndex(null);
    localStorage.removeItem('selectedHomeIndex');
  };

  const handleConfirm = async () => {
    const token = localStorage.getItem('token');
    if (!token) return onSwitch && onSwitch('login');
    if (selectedHomeIndex === null) {
      setErrorMessage('No home selected');
      return;
    }
    if (!selectedRoom) {
      setErrorMessage('Please select a room');
      return;
    }
    const home = homes[selectedHomeIndex];
    const baseApplianceName = selectedAppliance || 'Custom Appliance';
    const existingAppliances = homes[selectedHomeIndex]?.appliances || [];
    const sameNameCount = existingAppliances.filter((a) => a.name.startsWith(baseApplianceName)).length;
    const finalName = sameNameCount > 0 ? `${baseApplianceName} ${sameNameCount + 1}` : baseApplianceName;

    let calculatedEnergy;
    if (entryType === 'manual') {
      calculatedEnergy = energy ? Number(energy) : undefined;
    } else if (entryType === 'appliance') {
      const baseAppliance = defaultAppliances.find((a) => a.name === baseApplianceName);
      calculatedEnergy = baseAppliance ? baseAppliance.defaultEnergy : undefined;
    }

    const payload = {
      homeId: home.id || home._id,
      roomId: selectedRoom,
      name: finalName,
      type: baseApplianceName,
      energy_threshold: calculatedEnergy,
    };

    try {
      const res = await fetch(`${API_BASE}/appliances`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Create appliance failed');

      const baseAppliance = defaultAppliances.find((a) => a.name === baseApplianceName);
      setHomes((prev) => {
        const copy = [...prev];
        const existing = copy[selectedHomeIndex]?.appliances || [];
        copy[selectedHomeIndex] = {
          ...copy[selectedHomeIndex],
          appliances: [
            ...existing,
            {
              id: data._id || data.id,
              name: data.name,
              type: data.type,
              image: baseAppliance ? baseAppliance.image : '',
            },
          ],
        };
        return copy;
      });
      setErrorMessage('');
    } catch (err) {
      setErrorMessage(err.message || 'Failed to add appliance');
    }

    setShowAddModal(false);
    setSelectedAppliance('');
    setSelectedRoom('');
    setEnergy('');
  };

  const handleAddHome = async () => {
    const name = newHomeName.trim();
    if (!name) return;
    const token = localStorage.getItem('token');
    if (!token) return onSwitch && onSwitch('login');
    try {
      const res = await fetch(`${API_BASE}/homes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` },
        body: JSON.stringify({ name }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Create home failed');

      setHomes((prev) => {
        const newHomes = [...prev, { id: data.id || data._id, name: data.name, appliances: [] }];
        setSelectedHomeIndex(newHomes.length - 1);
        return newHomes;
      });
      setNewHomeName('');
      setShowAddHomeModal(false);
    } catch (err) {
      setErrorMessage(err.message || 'Failed to add home');
    }
  };

  const handleDeleteHome = async (index) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    const home = homes[index];
    try {
      const res = await fetch(`${API_BASE}/homes/${home.id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error((await res.json()).error || 'Delete home failed');
      setHomes((prev) => prev.filter((_, i) => i !== index));
      if (selectedHomeIndex === index) {
        setSelectedHomeIndex(null);
        localStorage.removeItem('selectedHomeIndex');
      } else if (selectedHomeIndex > index) {
        setSelectedHomeIndex(selectedHomeIndex - 1);
      }
      setShowDeleteHomeConfirm(false);
      setHomeToDelete(null);
    } catch (err) {
      setErrorMessage(err.message || 'Failed to delete home');
    }
  };

  const handleDeleteAppliance = async (applianceId, applianceIndex) => {
    const token = localStorage.getItem('token');
    if (!token) return;
    try {
      const res = await fetch(`${API_BASE}/appliances/${applianceId}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error((await res.json()).error || 'Delete appliance failed');
      setHomes((prev) => {
        const copy = [...prev];
        copy[selectedHomeIndex].appliances = copy[selectedHomeIndex].appliances.filter((_, i) => i !== applianceIndex);
        return copy;
      });
      setShowDeleteApplianceConfirm(false);
      setApplianceToDelete(null);
    } catch (err) {
      setErrorMessage(err.message || 'Failed to delete appliance');
    }
  };

  return (
    <div className="min-h-screen w-screen relative overflow-auto bg-gradient-to-br from-blue-50 via-white to-blue-50 font-sans">
      {/* Background */}
      <div
        className="absolute inset-0 bg-cover bg-center opacity-20"
        style={{ backgroundImage: "url('https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=2000&q=80')" }}
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />

      {/* Sidebar */}
      <aside className="fixed left-0 top-0 h-full z-40 flex items-center">
        <div className="relative h-64 w-32 flex items-center group">
          <div className="absolute left-0 top-1/2 -translate-y-1/2 w-12 h-12 bg-blue-600 rounded-r-full flex items-center justify-center shadow-lg transition-all duration-300 z-10 group-hover:opacity-0 group-hover:-translate-x-8">
            <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </div>
          <div className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-16 opacity-0 invisible group-hover:translate-x-8 group-hover:opacity-100 group-hover:visible transition-all duration-300 ease-out">
            <div className="bg-white/95 backdrop-blur-md p-4 rounded-xl shadow-2xl flex flex-col items-center border border-gray-200">
              <NavItem onClick={() => onSwitch('dashboard')} src={dashboardIcon} label="Dashboard" />
              <NavItem onClick={() => onSwitch('appliance')} src={applianceIcon} label="Appliances" />
              <NavItem onClick={() => onSwitch('profile')} src={profileIcon} label="Profile" />
              <NavItem onClick={() => setShowLogoutConfirm(true)} src={logoutIcon} label="Logout" />
            </div>
          </div>
        </div>
      </aside>

      {/* Error Toast */}
      {errorMessage && (
        <div className="fixed top-4 right-4 bg-red-600 text-white px-5 py-3 rounded-lg shadow-lg z-50 flex items-center gap-3 animate-pulse">
          <span>{errorMessage}</span>
          <button onClick={() => setErrorMessage('')} className="underline text-sm">Close</button>
        </div>
      )}

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-start pt-10 pb-16 px-4">
        <h1 className="text-4xl md:text-5xl font-bold text-gray-800 mb-10 tracking-wide">
          {selectedHomeIndex === null ? 'MY HOMES' : homes[selectedHomeIndex]?.name || 'HOME'}
        </h1>

        <div className="w-full max-w-7xl">
          {selectedHomeIndex === null ? (
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-6">
              <AddHomeCard onClick={() => setShowAddHomeModal(true)} />
              {homes.map((home, index) => (
                <HomeCard
                  key={home.id}
                  home={home}
                  onSelect={() => handleSelectHome(index)}
                  onDelete={() => {
                    setHomeToDelete({ name: home.name, index });
                    setShowDeleteHomeConfirm(true);
                  }}
                />
              ))}
            </div>
          ) : (
            <div>
              <div className="flex items-center mb-6">
                <button
                  onClick={handleBackToHomes}
                  className="flex items-center gap-2 text-blue-600 hover:text-blue-800 font-medium"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                  </svg>
                  Back to Homes
                </button>
              </div>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-6">
                <AddApplianceCard onClick={() => setShowAddModal(true)} />
                {(homes[selectedHomeIndex]?.appliances || []).map((appliance, index) => {
                  const baseAppliance = defaultAppliances.find((a) => a.name === appliance.type);
                  return (
                    <ApplianceCard
                      key={appliance.id}
                      name={appliance.name}
                      image={baseAppliance?.image || appliance.image}
                      onDelete={() => {
                        setApplianceToDelete({ id: appliance.id, name: appliance.name, index });
                        setShowDeleteApplianceConfirm(true);
                      }}
                    />
                  );
                })}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modals */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-sm w-full shadow-2xl">
            <h3 className="text-xl font-bold text-gray-800 mb-3">Confirm Logout</h3>
            <p className="text-gray-600 mb-6 text-center">Are you sure you want to logout?</p>
            <div className="flex justify-center gap-4">
              <button onClick={() => setShowLogoutConfirm(false)} className="px-6 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">Cancel</button>
              <button onClick={() => onSwitch('login')} className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700">Logout</button>
            </div>
          </div>
        </div>
      )}

      {/* Add Appliance Modal */}
      {showAddModal && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-md w-full shadow-2xl relative">
            <button onClick={() => setShowAddModal(false)} className="absolute top-3 left-3 text-gray-500 hover:text-gray-700">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>
            </button>
            <h3 className="text-2xl font-bold text-center mb-6">Add Appliance</h3>

            <div className="flex justify-center gap-6 mb-5">
              <label className="flex items-center cursor-pointer">
                <input type="radio" name="entry" value="manual" checked={entryType === 'manual'} onChange={(e) => setEntryType(e.target.value)} className="mr-2" />
                <span className="font-medium">Manual kWh</span>
              </label>
              <label className="flex items-center cursor-pointer">
                <input type="radio" name="entry" value="appliance" checked={entryType === 'appliance'} onChange={(e) => setEntryType(e.target.value)} className="mr-2" />
                <span className="font-medium">Preset</span>
              </label>
            </div>

            <div className="space-y-4">
              <select value={selectedAppliance} onChange={(e) => setSelectedAppliance(e.target.value)} className="w-full p-3 border rounded-lg">
                <option value="">Choose appliance</option>
                {defaultAppliances.map(a => <option key={a.id} value={a.name}>{a.name}</option>)}
              </select>

              <select value={selectedRoom} onChange={(e) => setSelectedRoom(e.target.value)} className="w-full p-3 border rounded-lg">
                <option value="">Select room</option>
                {rooms.map(r => <option key={r._id} value={r._id}>{r.name}</option>)}
              </select>

              {entryType === 'manual' && (
                <input type="number" value={energy} onChange={(e) => setEnergy(e.target.value)} placeholder="kWh threshold" className="w-full p-3 border rounded-lg" />
              )}
            </div>

            <button onClick={handleConfirm} className="mt-6 w-full bg-blue-600 text-white py-3 rounded-lg font-medium hover:bg-blue-700 transition">
              Add Appliance
            </button>
          </div>
        </div>
      )}

      {/* Add Home Modal */}
      {showAddHomeModal && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-md w-full shadow-2xl">
            <h3 className="text-2xl font-bold text-center mb-6">Add New Home</h3>
            <input
              type="text"
              value={newHomeName}
              onChange={(e) => setNewHomeName(e.target.value)}
              placeholder="e.g. Main House"
              className="w-full p-3 border rounded-lg mb-5"
            />
            <div className="flex gap-3">
              <button onClick={() => setShowAddHomeModal(false)} className="flex-1 py-3 border rounded-lg hover:bg-gray-50">Cancel</button>
              <button onClick={handleAddHome} className="flex-1 bg-green-600 text-white py-3 rounded-lg hover:bg-green-700">Create</button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmations */}
      {showDeleteHomeConfirm && homeToDelete && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-sm w-full shadow-2xl">
            <h3 className="text-xl font-bold text-red-600 mb-3">Delete Home?</h3>
            <p className="text-gray-600 mb-6">Permanently delete <strong>{homeToDelete.name}</strong> and all its appliances?</p>
            <div className="flex gap-3">
              <button onClick={() => { setShowDeleteHomeConfirm(false); setHomeToDelete(null); }} className="flex-1 py-2 border rounded-lg">Cancel</button>
              <button onClick={() => handleDeleteHome(homeToDelete.index)} className="flex-1 bg-red-600 text-white py-2 rounded-lg">Delete</button>
            </div>
          </div>
        </div>
      )}

      {showDeleteApplianceConfirm && applianceToDelete && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-sm w-full shadow-2xl">
            <h3 className="text-xl font-bold text-red-600 mb-3">Delete Appliance?</h3>
            <p className="text-gray-600 mb-6">Delete <strong>{applianceToDelete.name}</strong>?</p>
            <div className="flex gap-3">
              <button onClick={() => { setShowDeleteApplianceConfirm(false); setApplianceToDelete(null); }} className="flex-1 py-2 border rounded-lg">Cancel</button>
              <button onClick={() => handleDeleteAppliance(applianceToDelete.id, applianceToDelete.index)} className="flex-1 bg-red-600 text-white py-2 rounded-lg">Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Appliance;
