import React, { useEffect, useState } from "react";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  PieChart, Pie, Cell, BarChart, Bar, ResponsiveContainer
} from "recharts";

// Lightweight shadcn-style card components
const Card = ({ children, className = "" }) => (
  <div className={`bg-white rounded-xl shadow-xl overflow-hidden ${className}`}>{children}</div>
);
const CardHeader = ({ title }) => (
  <div className="bg-blue-500 text-white px-4 py-2 font-semibold rounded-t-xl">{title}</div>
);
const CardContent = ({ children }) => (
  <div className="p-4 bg-white flex items-center justify-center">{children}</div>
);

// Sidebar icon wrapper
const LeftIcon = ({ children, onClick }) => (
  <div
    onClick={onClick}
    className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60"
  >
    {children}
  </div>
);

const Dashboard = ({ onSwitch }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const bgUrl = "https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=2000&q=80";

  const [overallData, setOverallData] = useState([]);
  const [roomData, setRoomData] = useState([]);
  const [applianceData, setApplianceData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [seeding, setSeeding] = useState(false);
  const [currentHomeId, setCurrentHomeId] = useState(null);

  const API_BASE = 'http://localhost:5000/api';

  const handleSeedData = async () => {
    if (!currentHomeId) return;
    
    setSeeding(true);
    setError(null);
    
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`${API_BASE}/energy/seed?homeId=${currentHomeId}&days=30`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
      });
      
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to seed data');
      }
      
      const data = await res.json();
      alert(`Successfully generated ${data.readingsCount} energy readings!`);
      
      // Reload the dashboard data
      window.location.reload();
    } catch (err) {
      setError(err.message);
      setSeeding(false);
    }
  };

  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Not authenticated');
        setLoading(false);
        return;
      }

      try {
        // Fetch homes
        const homesRes = await fetch(`${API_BASE}/homes`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!homesRes.ok) throw new Error('Failed to fetch homes');
        const homes = await homesRes.json();

        if (homes.length === 0) {
          setLoading(false);
          return;
        }

        // Use the first home (or you can get selected home from localStorage like Appliance component)
        const selectedHomeIndex = localStorage.getItem('selectedHomeIndex');
        const homeIndex = selectedHomeIndex !== null && Number(selectedHomeIndex) < homes.length 
          ? Number(selectedHomeIndex) 
          : 0;
        const home = homes[homeIndex];
        setCurrentHomeId(home._id);

        // Fetch appliances for this home
        const appliancesRes = await fetch(`${API_BASE}/appliances?homeId=${home._id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!appliancesRes.ok) throw new Error('Failed to fetch appliances');
        const appliances = await appliancesRes.json();

        // Fetch energy readings for this home
        const readingsRes = await fetch(`${API_BASE}/energy?homeId=${home._id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!readingsRes.ok) throw new Error('Failed to fetch energy readings');
        const readings = await readingsRes.json();

        // Fetch rooms for this home
        const roomsRes = await fetch(`${API_BASE}/rooms?homeId=${home._id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!roomsRes.ok) throw new Error('Failed to fetch rooms');
        const rooms = await roomsRes.json();

        // Process data for charts
        processDashboardData(appliances, readings, rooms);
        
        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const processDashboardData = (appliances, readings, rooms) => {
    // Process appliance data for bar chart
    if (appliances.length > 0) {
      const applianceEnergyMap = new Map();
      
      readings.forEach(reading => {
        if (reading.applianceId && reading.energy) {
          const applianceName = reading.applianceId.name || 'Unknown';
          applianceEnergyMap.set(applianceName, (applianceEnergyMap.get(applianceName) || 0) + reading.energy);
        }
      });

      const applianceChartData = Array.from(applianceEnergyMap.entries())
        .map(([name, value]) => ({ name, value: parseFloat(value.toFixed(2)) }))
        .sort((a, b) => b.value - a.value)
        .slice(0, 6); // Top 6 appliances

      setApplianceData(applianceChartData.length > 0 ? applianceChartData : 
        appliances.map(a => ({ name: a.name, value: 0 }))
      );
    }

    // Process room data for pie chart
    if (rooms.length > 0) {
      const roomEnergyMap = new Map();
      
      readings.forEach(reading => {
        if (reading.roomId && reading.energy) {
          const roomName = reading.roomId.name || 'Unknown';
          roomEnergyMap.set(roomName, (roomEnergyMap.get(roomName) || 0) + reading.energy);
        }
      });

      const roomChartData = Array.from(roomEnergyMap.entries())
        .map(([name, value]) => ({ name, value: parseFloat(value.toFixed(2)) }));

      setRoomData(roomChartData.length > 0 ? roomChartData : 
        rooms.map(r => ({ name: r.name, value: 0 }))
      );
    }

    // Process overall data for line chart (aggregate by month)
    const monthlyData = new Map();
    
    readings.forEach(reading => {
      if (reading.recorded_at && reading.energy) {
        const date = new Date(reading.recorded_at);
        const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
        
        if (!monthlyData.has(monthKey)) {
          monthlyData.set(monthKey, { month: monthKey, consumption: 0 });
        }
        
        const existing = monthlyData.get(monthKey);
        existing.consumption += reading.energy;
        monthlyData.set(monthKey, existing);
      }
    });

    const overallChartData = Array.from(monthlyData.values())
      .map(item => ({
        month: item.month,
        production: item.consumption, // For now, using consumption as production
        consumption: item.consumption,
      }))
      .sort((a, b) => a.month.localeCompare(b.month));

    setOverallData(overallChartData.length > 0 ? overallChartData : [
      { month: new Date().toISOString().slice(0, 7), production: 0, consumption: 0 }
    ]);
  };

  const COLORS = ["#4CAF50", "#FF9800", "#2196F3", "#FFEB3B", "#9C27B0", "#607D86"];

  // Calculate bills from energy data
  const calculateBills = () => {
    let totalEnergy = 0;
    let totalCost = 0;
    
    applianceData.forEach(app => {
      totalEnergy += app.value;
    });

    // Assuming 10 PHP per kWh (from BILLING_RATE)
    totalCost = totalEnergy * 10;
    const upcomingBill = totalCost * 0.1; // 10% of total as upcoming bill

    return {
      overall: totalCost.toFixed(2),
      upcoming: upcomingBill.toFixed(2)
    };
  };

  const bills = calculateBills();

  if (loading) {
    return (
      <div className="h-screen w-screen overflow-hidden relative font-sans">
        <div className="absolute inset-0 bg-cover bg-center" style={{ backgroundImage: `url(${bgUrl})` }} />
        <div className="absolute inset-0 bg-black opacity-60 backdrop-blur-sm" />
        <div className="relative z-10 h-full w-full flex items-center justify-center">
          <div className="text-white text-2xl">Loading dashboard...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="h-screen w-screen overflow-hidden relative font-sans">
        <div className="absolute inset-0 bg-cover bg-center" style={{ backgroundImage: `url(${bgUrl})` }} />
        <div className="absolute inset-0 bg-black opacity-60 backdrop-blur-sm" />
        <div className="relative z-10 h-full w-full flex items-center justify-center">
          <div className="text-red-300 text-xl bg-black bg-opacity-70 p-6 rounded-lg">
            Error: {error}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen w-screen overflow-hidden relative font-sans">
      {/* Background */}
      <div className="absolute inset-0 bg-cover bg-center" style={{ backgroundImage: `url(${bgUrl})` }} />
      <div className="absolute inset-0 bg-black opacity-60 backdrop-blur-sm" />

      {/* Sidebar */}
      <aside className="absolute left-6 top-1/3 transform -translate-y-1/3 z-20">
        <div className="flex flex-col items-center">
          <div className="bg-white bg-opacity-6 p-3 rounded-lg shadow-lg flex flex-col items-center">
            <LeftIcon onClick={() => onSwitch("dashboard")}>üè†</LeftIcon>
            <LeftIcon onClick={() => onSwitch("appliance")}>‚öôÔ∏è</LeftIcon>
            <LeftIcon onClick={() => onSwitch("profile")}>üë§</LeftIcon>
            <LeftIcon onClick={() => setShowLogoutConfirm(true)}>üö™</LeftIcon>
          </div>
        </div>
      </aside>

      {/* Logout modal */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm mx-4 w-full">
            <h3 className="text-xl font-semibold text-gray-900 mb-4">Confirm Logout</h3>
            <p className="text-gray-600 mb-6 text-center">Are you sure you want to logout?</p>
            <div className="flex justify-center items-center gap-4">
              <button
                onClick={() => setShowLogoutConfirm(false)}
                className="px-6 py-2 text-gray-600 hover:text-gray-800 rounded border border-gray-300 hover:border-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={() => onSwitch("login")}
                className="px-6 py-2 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Main content */}
      <div className="relative z-10 h-full w-full flex items-start justify-center overflow-y-auto">
        <div className="w-[94%] max-w-[1200px] mt-6 pb-6">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl tracking-wider font-bold text-white">DASHBOARD</h1>
            <button
              onClick={handleSeedData}
              disabled={seeding || !currentHomeId}
              className="bg-green-600 hover:bg-green-700 disabled:bg-gray-400 text-white px-6 py-2 rounded-lg font-semibold transition-colors"
            >
              {seeding ? 'Generating Data...' : 'üé≤ Generate Sample Data'}
            </button>
          </div>

          <div className="grid grid-cols-2 gap-6 mb-6">
            {/* Left column */}
            <div className="flex flex-col gap-6">
              <Card className="flex-1">
                <CardHeader title="OVERALL CONSUMPTION" />
                <CardContent>
                  {overallData.length > 0 ? (
                    <ResponsiveContainer width="100%" height={180}>
                      <LineChart data={overallData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="month" style={{ fontSize: '10px' }} />
                        <YAxis style={{ fontSize: '10px' }} />
                        <Tooltip />
                        <Legend wrapperStyle={{ fontSize: '11px' }} />
                        <Line type="monotone" dataKey="production" stroke="#2196F3" name="total energy production" strokeWidth={2} />
                        <Line type="monotone" dataKey="consumption" stroke="#FF5252" name="total energy consumption" strokeWidth={2} />
                      </LineChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="text-gray-500 text-center py-8">No data available</div>
                  )}
                </CardContent>
              </Card>

              {/* Bills */}
              <div className="grid grid-cols-2 gap-6">
                <div className="bg-white rounded-lg shadow-xl p-4">
                  <div className="text-sm text-gray-600">Overall Bill</div>
                  <div className="text-2xl font-bold mt-4">‚Ç±{bills.overall}</div>
                </div>
                <div className="bg-white rounded-lg shadow-xl p-4">
                  <div className="text-sm text-gray-600">Upcoming Bill</div>
                  <div className="text-2xl font-bold mt-4">‚Ç±{bills.upcoming}</div>
                </div>
              </div>
            </div>

            {/* Right column */}
            <div className="flex flex-col gap-6">
              <Card className="flex-1">
                <CardHeader title="CONSUMPTION BY ROOM" />
                <CardContent>
                  {roomData.length > 0 ? (
                    <ResponsiveContainer width="100%" height={180}>
                      <PieChart>
                        <Pie
                          data={roomData}
                          cx="50%"
                          cy="50%"
                          outerRadius={70}
                          dataKey="value"
                          label={({ name, value }) => `${name}: ${value}kWh`}
                          style={{ fontSize: '10px' }}
                        >
                          {roomData.map((_, i) => (
                            <Cell key={i} fill={COLORS[i % COLORS.length]} />
                          ))}
                        </Pie>
                        <Tooltip />
                      </PieChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="text-gray-500 text-center py-8">No room data available</div>
                  )}
                </CardContent>
              </Card>

              <Card className="flex-1">
                <CardHeader title="APPLIANCES" />
                <CardContent>
                  {applianceData.length > 0 ? (
                    <ResponsiveContainer width="100%" height={160}>
                      <BarChart data={applianceData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" style={{ fontSize: '10px' }} />
                        <YAxis style={{ fontSize: '10px' }} />
                        <Tooltip />
                        <Bar dataKey="value">
                          {applianceData.map((_, i) => (
                            <Cell key={i} fill={COLORS[i % COLORS.length]} />
                          ))}
                        </Bar>
                      </BarChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="text-gray-500 text-center py-8">No appliance data available</div>
                  )}
                </CardContent>
              </Card>
            </div>
          </div>

          {/* History */}
          <div className="bg-white rounded-xl shadow-xl overflow-hidden">
            <div className="bg-blue-500 text-white px-4 py-2 font-semibold rounded-t-xl">HISTORY</div>
            <div className="p-4 h-40 overflow-auto">
              {applianceData.length > 0 ? (
                <table className="w-full text-left border-collapse">
                  <thead>
                    <tr className="text-xs text-gray-500">
                      <th className="pb-2">APPLIANCE</th>
                      <th className="pb-2">ENERGY (kWh)</th>
                      <th className="pb-2">COST (‚Ç±)</th>
                      <th className="pb-2">PERCENTAGE</th>
                    </tr>
                  </thead>
                  <tbody>
                    {applianceData.map((appliance, index) => {
                      const totalEnergy = applianceData.reduce((sum, a) => sum + a.value, 0);
                      const percentage = totalEnergy > 0 ? ((appliance.value / totalEnergy) * 100).toFixed(1) : 0;
                      const cost = (appliance.value * 10).toFixed(2);
                      return (
                        <tr key={index} className="border-t">
                          <td className="py-3">{appliance.name}</td>
                          <td className="py-3">{appliance.value.toFixed(2)} kWh</td>
                          <td className="py-3">‚Ç±{cost}</td>
                          <td className="py-3">{percentage}%</td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              ) : (
                <div className="text-gray-500 text-center py-4">No history data available</div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
