import React, { useState } from 'react';

const Signup = ({ onSwitch }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const togglePasswordVisibility = (field) => {
    if (field === 'password') {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Email validation
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    // Password validation
    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    // Confirm password validation
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      console.log('Signup successful:', formData);
      alert('Account created successfully!');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative p-5">
      <div 
        className="fixed inset-0 bg-cover bg-center bg-no-repeat blur-lg -z-10"
        style={{
          backgroundImage: "url('https://images.unsplash.com/photo-1586023492125-27b2c045efd7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2016&q=80')"
        }}
      />
      <div className="absolute inset-0 bg-black bg-opacity-30 -z-10" />
      
      {/* Signup Modal */}
      <div className="bg-white rounded-3xl shadow-2xl overflow-hidden flex max-w-4xl w-full min-h-[600px]">
        {/* Left Section - Promotional */}
        <div className="flex-1 bg-gradient-to-br from-primary-500 to-primary-800 flex items-center justify-center relative overflow-hidden">
          <div className="text-center text-white p-10 relative z-10">
            <h1 className="text-4xl font-bold mb-5 leading-tight">
              Home Energy Monitoring
            </h1>
            <p className="text-xl font-normal mb-10 opacity-90">
              Empowering Homes, Saving Energy
            </p>
            <div className="text-6xl text-yellow-glow animate-glow">
              <i className="fas fa-lightbulb"></i>
            </div>
          </div>
        </div>
        
        {/* Right Section - Sign Up Form */}
        <div className="flex-1 p-16 flex items-center justify-center">
          <div className="w-full max-w-sm">
            <h2 className="text-3xl font-bold text-gray-800 mb-10 text-center">
              Sign up
            </h2>
            
            <form className="flex flex-col gap-6" onSubmit={handleSubmit}>
              {/* Email Input */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-envelope absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type="email"
                    name="email"
                    placeholder="Email Address"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-4 py-4 border-2 rounded-xl text-base transition-all duration-300 box-border ${
                      errors.email 
                        ? 'border-red-500 focus:border-red-500' 
                        : 'border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100'
                    }`}
                    required
                  />
                </div>
                {errors.email && (
                  <span className="text-red-500 text-sm mt-1">{errors.email}</span>
                )}
              </div>
              
              {/* Password Input */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-lock absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl text-base transition-all duration-300 box-border ${
                      errors.password 
                        ? 'border-red-500 focus:border-red-500' 
                        : 'border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100'
                    }`}
                    required
                  />
                  <button
                    type="button"
                    className="absolute right-4 bg-transparent border-none text-gray-400 cursor-pointer p-1 transition-colors duration-300 hover:text-gray-600"
                    onClick={() => togglePasswordVisibility('password')}
                  >
                    <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                  </button>
                </div>
                {errors.password && (
                  <span className="text-red-500 text-sm mt-1">{errors.password}</span>
                )}
              </div>
              
              {/* Confirm Password Input */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-lock absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type={showConfirmPassword ? 'text' : 'password'}
                    name="confirmPassword"
                    placeholder="Confirm Password"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl text-base transition-all duration-300 box-border ${
                      errors.confirmPassword 
                        ? 'border-red-500 focus:border-red-500' 
                        : 'border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100'
                    }`}
                    required
                  />
                  <button
                    type="button"
                    className="absolute right-4 bg-transparent border-none text-gray-400 cursor-pointer p-1 transition-colors duration-300 hover:text-gray-600"
                    onClick={() => togglePasswordVisibility('confirmPassword')}
                  >
                    <i className={`fas ${showConfirmPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                  </button>
                </div>
                {errors.confirmPassword && (
                  <span className="text-red-500 text-sm mt-1">{errors.confirmPassword}</span>
                )}
              </div>
              
              {/* Signup Button */}
              <button 
                type="submit" 
                className="bg-gradient-to-r from-primary-500 to-primary-700 text-white border-none py-4 px-8 rounded-xl text-base font-semibold cursor-pointer transition-all duration-300 mt-3 hover:-translate-y-0.5 hover:shadow-lg hover:shadow-primary-300 active:translate-y-0"
              >
                Sign up
              </button>
            </form>
            
            {/* Sign in Link */}
            <div className="text-center mt-8">
              <p className="text-gray-600 m-0">
                Already a member?{' '}
                <button
                  type="button"
                  onClick={() => onSwitch && onSwitch('login')}
                  className="text-primary-500 no-underline font-semibold transition-colors duration-300 hover:text-primary-700 bg-transparent border-none cursor-pointer p-0"
                >
                  Sign in
                </button>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
