import React, { useState, useEffect, useCallback } from 'react';
import dashboardIcon from '../assets/icons/dashboard.svg';
import applianceIcon from '../assets/icons/add.svg';
import profileIcon from '../assets/icons/profile.svg';
import logoutIcon from '../assets/icons/logout.svg';

const API_BASE = 'http://localhost:5000/api';
const UPLOADS_BASE = 'http://localhost:5000'; // Base for image URLs

/* -------------------------------------------------------------------------- */
/*                              REUSABLE COMPONENTS                           */
/* -------------------------------------------------------------------------- */
const NavItem = ({ onClick, src, label }) => (
  <div
    onClick={onClick}
    className="w-12 h-12 flex items-center justify-center rounded-lg mb-3 bg-blue-600 text-white cursor-pointer shadow-md hover:bg-blue-700 hover:shadow-lg transition-all duration-200"
    title={label}
  >
    <img src={src} alt={label} className="w-6 h-6" />
  </div>
);

const ProfileField = ({ label, value, icon }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-600 mb-1 flex items-center">
      {icon && <span className="mr-2">{icon}</span>}
      {label}
    </label>
    <div className="w-full p-3 bg-gray-50 border border-gray-200 rounded-lg text-gray-800 font-medium">
      {value || '—'}
    </div>
  </div>
);

/* -------------------------------------------------------------------------- */
/*                              EDIT PROFILE MODAL                             */
/* -------------------------------------------------------------------------- */
const EditProfileModal = ({ user, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    name: user.name || '',
    email: user.email || '',
    notification_preferences: user.notification_preferences || {
      email: true,
      push: false,
      in_app: true,
    },
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/users/profile`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (res.ok) {
        const updated = await res.json();
        onSave(updated);
        onClose();
      }
    } catch (err) {
      console.error('Failed to update profile', err);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full p-6">
        <h3 className="text-2xl font-bold text-center mb-6 text-gray-800">Edit Profile</h3>
        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
            <input
              type="email"
              value={formData.email}
              readOnly
              className="w-full p-3 bg-gray-100 border border-gray-300 rounded-lg cursor-not-allowed"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">Notifications</label>
            {['email', 'push', 'in_app'].map((key) => (
              <label key={key} className="flex items-center mb-2">
                <input
                  type="checkbox"
                  checked={formData.notification_preferences[key]}
                  onChange={(e) =>
                    setFormData({
                      ...formData,
                      notification_preferences: {
                        ...formData.notification_preferences,
                        [key]: e.target.checked,
                      },
                    })
                  }
                  className="mr-3 h-4 w-4 text-blue-600 rounded border-gray-300"
                />
                <span className="text-sm capitalize">{key.replace('_', ' ')} Alerts</span>
              </label>
            ))}
          </div>

          <div className="flex justify-end gap-3 pt-4">
            <button type="button" onClick={onClose} className="px-5 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">
              Cancel
            </button>
            <button type="submit" className="px-5 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
              Save Changes
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

/* -------------------------------------------------------------------------- */
/*                                 MAIN PROFILE                               */
/* -------------------------------------------------------------------------- */
const Profile = ({ onSwitch }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  const [user, setUser] = useState({
    name: '',
    email: '',
    profilePicture: null,
    notification_preferences: { email: true, push: false, in_app: true },
  });

  const [activity, setActivity] = useState({
    locations: 0,
    appliances: 0,
    logs: 0,
    anomalies: 0,
  });

  /* ------------------------------- FETCH DATA ------------------------------- */
  const fetchProfileData = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    setLoading(true);
    try {
      const headers = { Authorization: `Bearer ${token}` };

      const [
        profileRes,
        homesRes,
        appliancesRes,
        energyRes,
        anomaliesRes,
      ] = await Promise.all([
        fetch(`${API_BASE}/users/profile`, { headers }),
        fetch(`${API_BASE}/homes`, { headers }),
        fetch(`${API_BASE}/appliances`, { headers }),
        fetch(`${API_BASE}/energy`, { headers }),
        fetch(`${API_BASE}/anomalies?limit=100`, { headers }),
      ]);

      const profile = profileRes.ok ? await profileRes.json() : {};
      const homes = homesRes.ok ? await homesRes.json() : [];
      const appliances = appliancesRes.ok ? await appliancesRes.json() : [];
      const readings = energyRes.ok ? await energyRes.json() : [];
      const anomalies = anomaliesRes.ok ? await anomaliesRes.json() : [];

      // Build full image URL
      const picturePath = profile.profilePicture;
      const fullPictureUrl = picturePath
        ? `${UPLOADS_BASE}${picturePath.startsWith('/') ? '' : '/'}${picturePath}`
        : null;

      setUser({
        name: profile.name || '',
        email: profile.email || '',
        profilePicture: fullPictureUrl,
        notification_preferences: profile.notification_preferences || {
          email: true,
          push: false,
          in_app: true,
        },
      });

      setActivity({
        locations: Array.isArray(homes) ? homes.length : 0,
        appliances: Array.isArray(appliances) ? appliances.length : 0,
        logs: Array.isArray(readings) ? readings.length : 0,
        anomalies: Array.isArray(anomalies) ? anomalies.length : 0,
      });
    } catch (err) {
      console.error('Failed to load profile', err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchProfileData();
  }, [fetchProfileData]);

  /* ----------------------------- IMAGE UPLOAD ----------------------------- */
  const handleImageUpload = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('profilePicture', file);
    setUploading(true);

    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/users/profile/picture`, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });
      if (res.ok) {
        const updated = await res.json();
        const fullUrl = `${UPLOADS_BASE}${updated.profilePicture.startsWith('/') ? '' : '/'}${updated.profilePicture}`;
        setUser((u) => ({ ...u, profilePicture: fullUrl }));
      }
    } catch (err) {
      console.error('Upload failed', err);
    } finally {
      setUploading(false);
    }
  };

  const handleDeletePicture = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`${API_BASE}/users/profile/picture`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        setUser((u) => ({ ...u, profilePicture: null }));
      }
    } catch (err) {
      console.error('Delete failed', err);
    }
  };

  /* --------------------------------- RENDER -------------------------------- */
  const bgUrl = "https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=2000&q=80";

  return (
    <div className="min-h-screen w-screen relative overflow-auto bg-gradient-to-br from-blue-50 via-white to-blue-50 font-sans">
      {/* Background */}
      <div
        className="absolute inset-0 bg-cover bg-center opacity-20"
        style={{ backgroundImage: `url(${bgUrl})` }}
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />

      {/* Sidebar */}
      <aside className="fixed left-0 top-0 h-full z-40 flex items-center">
        <div className="relative h-64 w-32 flex items-center group">
          <div className="absolute left-0 top-1/2 -translate-y-1/2 w-12 h-12 bg-blue-600 rounded-r-full flex items-center justify-center shadow-lg transition-all duration-300 z-10 group-hover:opacity-0 group-hover:-translate-x-8">
            <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </div>
          <div className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-16 opacity-0 invisible group-hover:translate-x-8 group-hover:opacity-100 group-hover:visible transition-all duration-300 ease-out">
            <div className="bg-white/95 backdrop-blur-md p-4 rounded-xl shadow-2xl flex flex-col items-center border border-gray-200">
              <NavItem onClick={() => onSwitch('dashboard')} src={dashboardIcon} label="Dashboard" />
              <NavItem onClick={() => onSwitch('appliance')} src={applianceIcon} label="Appliances" />
              <NavItem onClick={() => onSwitch('profile')} src={profileIcon} label="Profile" />
              <NavItem onClick={() => setShowLogoutConfirm(true)} src={logoutIcon} label="Logout" />
            </div>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-start pt-10 pb-16 px-4">
        <h1 className="text-4xl md:text-5xl font-bold text-gray-800 mb-10 tracking-wide">PROFILE</h1>

        <div className="w-full max-w-7xl">
          {loading ? (
            <div className="flex justify-center items-center h-64">
              <div className="text-xl text-gray-600 animate-pulse">Loading profile...</div>
            </div>
          ) : (
            <>
              {/* User Header */}
              <div className="bg-white/90 backdrop-blur-sm rounded-2xl p-6 mb-8 shadow-xl border border-gray-100 flex flex-col md:flex-col lg:flex-row items-center justify-between gap-6">
                <div className="flex items-center gap-6">
                  <div className="relative">
                    {user.profilePicture ? (
                      <img
                        src={user.profilePicture}
                        alt="Profile"
                        className="w-28 h-28 rounded-full border-4 border-white shadow-lg object-cover"
                      />
                    ) : (
                      <div className="w-28 h-28 rounded-full bg-gradient-to-br from-blue-400 to-blue-600 flex items-center justify-center text-4xl font-bold text-white shadow-lg">
                        {user.name?.[0]?.toUpperCase() || '?'}
                      </div>
                    )}
                    {uploading && (
                      <div className="absolute inset-0 bg-black/50 rounded-full flex items-center justify-center">
                        <div className="animate-spin rounded-full h-8 w-8 border-2 border-white border-t-transparent"></div>
                      </div>
                    )}
                  </div>
                  <div className="text-left">
                    <h2 className="text-2xl font-bold text-gray-800">{user.name || 'User'}</h2>
                    <p className="text-blue-600 font-medium">{user.email || '—'}</p>
                  </div>
                </div>

                <div className="flex flex-wrap gap-3 justify-center">
                  <label className="cursor-pointer">
                    <input type="file" accept="image/*" onChange={handleImageUpload} className="hidden" />
                    <div className="px-5 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center gap-2 shadow-md">
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0l-4 4m4-4v12" />
                      </svg>
                      Upload Photo
                    </div>
                  </label>

                  {user.profilePicture && (
                    <button
                      onClick={handleDeletePicture}
                      className="px-5 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 flex items-center gap-2 shadow-md"
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                      </svg>
                      Delete
                    </button>
                  )}

                  <button
                    onClick={() => setShowEditModal(true)}
                    className="px-5 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center gap-2 shadow-md"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h12a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                    </svg>
                    Edit Profile
                  </button>
                </div>
              </div>

              {/* Two Column Layout */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                {/* Personal Information */}
                <div className="bg-white rounded-2xl shadow-xl overflow-hidden border border-gray-100">
                  <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-4 font-bold text-lg">
                    Personal Information
                  </div>
                  <div className="p-6 space-y-5">
                    <ProfileField label="Full Name" value={user.name} />
                    <ProfileField label="Email Address" value={user.email} />

                    <div className="pt-3">
                      <h4 className="text-sm font-semibold text-gray-700 mb-3">Notification Preferences</h4>
                      <div className="space-y-3 text-sm">
                        {Object.entries(user.notification_preferences).map(([key, val]) => (
                          <div key={key} className="flex justify-between items-center">
                            <span className="capitalize">{key.replace('_', ' ')}</span>
                            <span
                              className={`px-3 py-1 rounded-full text-xs font-medium ${
                                val ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
                              }`}
                            >
                              {val ? 'ON' : 'OFF'}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>

                {/* Activity Overview */}
                <div className="bg-white rounded-2xl shadow-xl overflow-hidden border border-gray-100">
                  <div className="bg-gradient-to-r from-purple-500 to-purple-600 text-white px-6 py-4 font-bold text-lg">
                    Activity Overview
                  </div>
                  <div className="p-6 grid grid-cols-2 gap-5">
                    {[
                      { label: 'Location(s)', value: activity.locations, color: 'blue' },
                      { label: 'Appliance(s)', value: activity.appliances, color: 'green' },
                      { label: 'Energy Log(s)', value: activity.logs, color: 'yellow' },
                      { label: 'Anomalie(s)', value: activity.anomalies, color: 'red' },
                    ].map((item) => (
                      <div
                        key={item.label}
                        className={`bg-gradient-to-br from-${item.color}-50 to-${item.color}-100 p-5 rounded-xl text-center transform hover:scale-105 transition-transform shadow-md`}
                      >
                        <div className={`text-3xl font-bold text-${item.color}-700`}>{item.value}</div>
                        <div className={`text-sm text-${item.color}-600 mt-1 font-medium`}>{item.label}</div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </>
          )}
        </div>
      </div>

      {/* Logout Confirmation */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 max-w-sm w-full shadow-2xl">
            <h3 className="text-xl font-bold text-gray-800 mb-3">Confirm Logout</h3>
            <p className="text-gray-600 mb-6 text-center">Are you sure you want to logout?</p>
            <div className="flex justify-center gap-4">
              <button onClick={() => setShowLogoutConfirm(false)} className="px-6 py-2 border border-gray-300 rounded-lg hover:bg-gray-50">
                Cancel
              </button>
              <button onClick={() => onSwitch('login')} className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700">
                Logout
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Modal */}
      {showEditModal && (
        <EditProfileModal
          user={user}
          onClose={() => setShowEditModal(false)}
          onSave={(updated) => {
            const picPath = updated.profilePicture;
            const fullPic = picPath
              ? `${UPLOADS_BASE}${picPath.startsWith('/') ? '' : '/'}${picPath}`
              : null;
            setUser({ ...updated, profilePicture: fullPic });
            setShowEditModal(false);
          }}
        />
      )}
    </div>
  );
};

export default Profile;
