import React, { useState } from "react";

const Signup = ({ onSwitch }) => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  // üß© Input change handler
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }));
    }
  };

  // üëÅÔ∏è Toggle password visibility
  const togglePasswordVisibility = (field) => {
    if (field === "password") {
      setShowPassword(!showPassword);
    } else {
      setShowConfirmPassword(!showConfirmPassword);
    }
  };

  // üß† Validate input fields
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name) newErrors.name = "Full name is required";

    if (!formData.email) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Email is invalid";
    }

    if (!formData.password) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = "Please confirm your password";
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // üöÄ Handle form submission and connect to backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      setLoading(true);
      const response = await fetch("http://localhost:5000/api/users/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert("‚úÖ Account created successfully!");
        console.log("User created:", data);
        setFormData({
          name: "",
          email: "",
          password: "",
          confirmPassword: "",
        });
      } else {
        alert(data.error || "‚ùå Registration failed.");
        console.error("Server error:", data);
      }
    } catch (err) {
      alert("‚ö†Ô∏è Server connection failed. Please try again later.");
      console.error("Request error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center relative p-5">
      <div
        className="fixed inset-0 bg-cover bg-center bg-no-repeat blur-lg -z-10"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1586023492125-27b2c045efd7?auto=format&fit=crop&w=2016&q=80')",
        }}
      />
      <div className="absolute inset-0 bg-black bg-opacity-30 -z-10" />

      <div className="bg-white rounded-3xl shadow-2xl overflow-hidden flex max-w-4xl w-full min-h-[600px]">
        {/* Left Section */}
        <div className="flex-1 bg-gradient-to-br from-primary-500 to-primary-800 flex items-center justify-center">
          <div className="text-center text-white p-10 relative z-10">
            <h1 className="text-4xl font-bold mb-5 leading-tight">
              Home Energy Monitoring
            </h1>
            <p className="text-xl mb-10 opacity-90">
              Empowering Homes, Saving Energy
            </p>
            <div className="text-6xl text-yellow-glow animate-glow">
              <i className="fas fa-lightbulb"></i>
            </div>
          </div>
        </div>

        {/* Right Section */}
        <div className="flex-1 p-16 flex items-center justify-center">
          <div className="w-full max-w-sm">
            <h2 className="text-3xl font-bold text-gray-800 mb-10 text-center">
              Sign up
            </h2>

            <form className="flex flex-col gap-6" onSubmit={handleSubmit}>
              {/* Full Name */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-user absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type="text"
                    name="name"
                    placeholder="Full Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-4 py-4 border-2 rounded-xl text-base transition-all duration-300 ${
                      errors.name
                        ? "border-red-500 focus:border-red-500"
                        : "border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100"
                    }`}
                    required
                  />
                </div>
                {errors.name && (
                  <span className="text-red-500 text-sm mt-1">
                    {errors.name}
                  </span>
                )}
              </div>

              {/* Email */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-envelope absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type="email"
                    name="email"
                    placeholder="Email Address"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-4 py-4 border-2 rounded-xl text-base transition-all duration-300 ${
                      errors.email
                        ? "border-red-500 focus:border-red-500"
                        : "border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100"
                    }`}
                    required
                  />
                </div>
                {errors.email && (
                  <span className="text-red-500 text-sm mt-1">
                    {errors.email}
                  </span>
                )}
              </div>

              {/* Password */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-lock absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl text-base transition-all duration-300 ${
                      errors.password
                        ? "border-red-500 focus:border-red-500"
                        : "border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100"
                    }`}
                    required
                  />
                  <button
                    type="button"
                    className="absolute right-4 text-gray-400 hover:text-gray-600"
                    onClick={() => togglePasswordVisibility("password")}
                  >
                    <i
                      className={`fas ${
                        showPassword ? "fa-eye-slash" : "fa-eye"
                      }`}
                    ></i>
                  </button>
                </div>
                {errors.password && (
                  <span className="text-red-500 text-sm mt-1">
                    {errors.password}
                  </span>
                )}
              </div>

              {/* Confirm Password */}
              <div className="flex flex-col gap-2">
                <div className="relative flex items-center">
                  <i className="fas fa-lock absolute left-4 text-gray-400 z-10"></i>
                  <input
                    type={showConfirmPassword ? "text" : "password"}
                    name="confirmPassword"
                    placeholder="Confirm Password"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className={`w-full pl-12 pr-12 py-4 border-2 rounded-xl text-base transition-all duration-300 ${
                      errors.confirmPassword
                        ? "border-red-500 focus:border-red-500"
                        : "border-gray-200 focus:border-primary-500 focus:shadow-lg focus:shadow-primary-100"
                    }`}
                    required
                  />
                  <button
                    type="button"
                    className="absolute right-4 text-gray-400 hover:text-gray-600"
                    onClick={() =>
                      togglePasswordVisibility("confirmPassword")
                    }
                  >
                    <i
                      className={`fas ${
                        showConfirmPassword ? "fa-eye-slash" : "fa-eye"
                      }`}
                    ></i>
                  </button>
                </div>
                {errors.confirmPassword && (
                  <span className="text-red-500 text-sm mt-1">
                    {errors.confirmPassword}
                  </span>
                )}
              </div>

              {/* Submit */}
              <button
                type="submit"
                disabled={loading}
                className="bg-gradient-to-r from-primary-500 to-primary-700 text-white py-4 px-8 rounded-xl font-semibold hover:-translate-y-0.5 hover:shadow-lg transition-all"
              >
                {loading ? "Creating Account..." : "Sign up"}
              </button>
            </form>

            <div className="text-center mt-8">
              <p className="text-gray-600">
                Already a member?{" "}
                <button
                  type="button"
                  onClick={() => onSwitch && onSwitch("login")}
                  className="text-primary-500 font-semibold hover:text-primary-700"
                >
                  Sign in
                </button>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
