import React, { useState, useEffect } from "react";
import Signup from "./components/Signup";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Dashboard from "./components/Dashboard";
import Appliance from "./components/Appliance";
import Profile from "./components/Profile";

function App() {
  const [page, setPage] = useState("login");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    console.log("ðŸ”„ App loading...");

    const token = localStorage.getItem("token");
    const savedPage = localStorage.getItem("currentPage") || "dashboard";

    if (token) {
      console.log("âœ… Found token, restoring session");
      setIsAuthenticated(true);
      setPage(savedPage);
    } else {
      console.log("âŒ No token found, showing login");
      setIsAuthenticated(false);
      setPage("login");
    }

    setIsLoading(false); // always end loading state
  }, []);

  const handleSwitch = (p) => {
    setPage(p);
    localStorage.setItem("currentPage", p);
  };

  const handleLogout = () => {
    console.log("ðŸšª Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("currentPage");
    setIsAuthenticated(false);
    setPage("login");
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen text-lg">
        Loading...
      </div>
    );
  }

  return (
    <div className="text-center min-h-screen">
      {!isAuthenticated ? (
        <>
          {page === "signup" && <Signup onSwitch={handleSwitch} />}
          {page === "login" && (
            <Login
              onSwitch={(newPage) => {
                if (newPage === "dashboard") {
                  setIsAuthenticated(true);
                }
                handleSwitch(newPage);
              }}
            />
          )}
          {page === "forgot" && <ForgotPassword onSwitch={handleSwitch} />}
        </>
      ) : (
        <>
          {page === "dashboard" && (
            <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "appliance" && (
            <Appliance onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "profile" && (
            <Profile onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
