import React, { useState, useEffect } from "react";
import Signup from "./components/Signup";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Dashboard from "./components/Dashboard";
import Appliance from "./components/Appliance";
import Profile from "./components/Profile";

function App() {
  const [page, setPage] = useState("login");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const savedPage = localStorage.getItem("currentPage") || "dashboard";

    if (token) {
      setIsAuthenticated(true);
      setPage(savedPage);
    } else {
      setIsAuthenticated(false);
      setPage("login");
    }

    setIsLoading(false);
  }, []);

  const handleSwitch = (newPage) => {
    setPage(newPage);
    localStorage.setItem("currentPage", newPage);
  };

  const handleLogout = () => {
    console.log("ðŸšª Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("currentPage");

    // Force state reset to login view
    setIsAuthenticated(false);
    setPage("login");
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen text-lg">
        Loading...
      </div>
    );
  }

  // ðŸ”¥ Render logic (clean separation)
  if (!isAuthenticated) {
    if (page === "signup") return <Signup onSwitch={handleSwitch} />;
    if (page === "forgot") return <ForgotPassword onSwitch={handleSwitch} />;
    return (
      <Login
        onSwitch={(nextPage) => {
          if (nextPage === "dashboard") {
            setIsAuthenticated(true);
            setPage("dashboard");
          } else {
            handleSwitch(nextPage);
          }
        }}
      />
    );
  }

  // Authenticated section
  switch (page) {
    case "dashboard":
      return <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />;
    case "appliance":
      return <Appliance onSwitch={handleSwitch} onLogout={handleLogout} />;
    case "profile":
      return <Profile onSwitch={handleSwitch} onLogout={handleLogout} />;
    default:
      return <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />;
  }
}

export default App;
