import React, { useState } from 'react';

const ApplianceCard = ({ name, image }) => (
  <div className="bg-white rounded-lg shadow-sm p-4 flex flex-col items-center justify-center hover:shadow-md transition-shadow cursor-pointer">
    <img src={image} alt={name} className="w-16 h-16 object-contain mb-2" />
    <div className="text-sm text-gray-600">{name}</div>
  </div>
);

const AddApplianceCard = () => (
  <div className="bg-white rounded-lg shadow-sm p-4 flex flex-col items-center justify-center hover:shadow-md transition-shadow cursor-pointer">
    <div className="w-16 h-16 flex items-center justify-center">
      <svg width="32" height="32" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M12 5v14m-7-7h14" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
      </svg>
    </div>
    <div className="text-sm text-gray-600">Add new appliance</div>
  </div>
);

const Appliance = ({ onSwitch }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const appliances = [
    { 
      name: 'Fridge',
      image: 'data:image/svg+xml;base64,'
    },
    {
      name: 'Television',
      image: 'data:image/svg+xml;base64,'
    },
    {
      name: 'Air Conditioner',
      image: 'data:image/svg+xml;base64,'
    },
    {
      name: 'Lights',
      image: 'data:image/svg+xml;base64,'
    },
    {
      name: 'Speaker',
      image: 'data:image/svg+xml;base64,'
    },
  ];

  return (
    <div className="h-screen w-screen overflow-hidden relative font-sans">
      {/* Background with overlay */}
      <div className="absolute inset-0 bg-[url('https://images.unsplash.com/photo-1482192505345-5655af888cc4?auto=format&fit=crop&w=2000&q=80')] bg-cover bg-center" />
      <div className="absolute inset-0 bg-black opacity-60 backdrop-blur-sm" />

      {/* Left Navigation */}
      <aside className="absolute left-6 top-1/3 transform -translate-y-1/3 z-20">
        <div className="flex flex-col items-center">
          <div className="bg-white bg-opacity-6 p-3 rounded-lg shadow-lg flex flex-col items-center">
            {/* Dashboard */}
            <div onClick={() => onSwitch('dashboard')} className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <rect x="3" y="3" width="7" height="7" stroke="currentColor" strokeWidth="1.5" rx="1"/>
              </svg>
            </div>
            {/* Appliance */}
            <div onClick={() => onSwitch('appliance')} className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <rect x="3" y="3" width="18" height="18" rx="2" stroke="currentColor" strokeWidth="1.5"/>
              </svg>
            </div>
            {/* Profile */}
            <div onClick={() => onSwitch('profile')} className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <circle cx="12" cy="8" r="3" stroke="currentColor" strokeWidth="1.5"/>
                <path d="M4 20c1-4 7-6 8-6s7 2 8 6" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round"/>
              </svg>
            </div>
            {/* Logout */}
            <div onClick={() => onSwitch('login')} className="w-12 h-12 flex items-center justify-center rounded-md mb-3 bg-black bg-opacity-40 text-white cursor-pointer hover:bg-opacity-60">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M19 21H5a2 2 0 0 1-2-2V7" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M7 11h10M7 7h10" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <div className="relative z-10 h-full w-full flex items-start justify-center">
        <div className="w-[94%] max-w-[1200px] mt-6">
          <div className="text-center mb-8">
            <h1 className="text-3xl tracking-wider font-bold text-white">APPLIANCES</h1>
          </div>

          {/* Appliance Grid */}
          <div className="bg-white rounded-xl shadow-xl overflow-hidden">
            <div className="bg-blue-500 text-white px-4 py-2 font-semibold rounded-t-xl flex items-center justify-between">
              <span>All</span>
            </div>
            <div className="p-6">
              <div className="grid grid-cols-6 gap-4">
                <AddApplianceCard />
                {appliances.map((appliance, index) => (
                  <ApplianceCard
                    key={index}
                    name={appliance.name}
                    image={appliance.image}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Appliance;
