const Appliance = ({ onSwitch }) => {
  return (
    <div className="h-screen w-screen flex items-center justify-center bg-gray-100">
      <div className="w-[90%] max-w-4xl bg-white rounded-xl shadow p-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Appliances</h2>
        <p className="text-gray-600 mb-6">Placeholder appliances page â€” replace with appliances controls and charts.</p>
        <div className="flex justify-center gap-4">
          <button onClick={() => onSwitch && onSwitch('dashboard')} className="px-4 py-2 bg-blue-500 text-white rounded">Back to Dashboard</button>
        </div>
      </div>
    </div>
  );
};

export default Appliance;
