import React, { useState, useEffect } from "react";
import Signup from "./components/Signup";
import Login from "./components/Login";
import ForgotPassword from "./components/ForgotPassword";
import Dashboard from "./components/Dashboard";
import Appliance from "./components/Appliance";
import Profile from "./components/Profile";

function App() {
  const [page, setPage] = useState("login");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isCheckingAuth, setIsCheckingAuth] = useState(true);
  const [hasInitialized, setHasInitialized] = useState(false); 

  useEffect(() => {
    console.log("ðŸ”„ Checking authentication...");
    const token = localStorage.getItem("token");
    let savedPage = localStorage.getItem("currentPage");

    console.log("Token:", token ? "exists" : "missing");
    console.log("SavedPage:", savedPage);

    if (token) {
      setIsAuthenticated(true);
      // If token exists, keep the saved page if it's a protected route
      if (savedPage && ["dashboard", "appliance", "profile"].includes(savedPage)) {
        setPage(savedPage);
      } else {
        setPage("dashboard");
        localStorage.setItem("currentPage", "dashboard");
      }
    } else {
      setIsAuthenticated(false);
      // No token, so check if savedPage is a protected route
      // If it is (dashboard/appliance/profile), redirect to login
      if (savedPage && ["dashboard", "appliance", "profile"].includes(savedPage)) {
        setPage("login");
        localStorage.setItem("currentPage", "login");
      } else if (savedPage && ["login", "signup", "forgot"].includes(savedPage)) {
        // If already on auth pages, stay there
        setPage(savedPage);
      } else {
        // Default to login
        setPage("login");
        localStorage.setItem("currentPage", "login");
      }
    }

    setIsCheckingAuth(false);
    setHasInitialized(true);
  }, []);

  const handleSwitch = (newPage) => {
    console.log("ðŸ“„ Switching to page:", newPage);
    setPage(newPage);
    
    // Save all pages to localStorage, not just protected ones
    localStorage.setItem("currentPage", newPage);
  };

  const handleLogout = () => {
    console.log("ðŸšª Logging out...");
    localStorage.removeItem("token");
    localStorage.setItem("currentPage", "login");
    setIsAuthenticated(false);
    setPage("login");
  };

  if (isCheckingAuth || !hasInitialized) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-white text-gray-500">
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="text-center min-h-screen">
      {/* Show login pages regardless of authenticated state if page is set to them */}
      {(page === "signup" || page === "login" || page === "forgot") ? (
        <>
          {page === "signup" && <Signup onSwitch={handleSwitch} />}
          {page === "login" && <Login onSwitch={handleSwitch} />}
          {page === "forgot" && <ForgotPassword onSwitch={handleSwitch} />}
        </>
      ) : (
        <>
          {page === "dashboard" && (
            <Dashboard onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "appliance" && (
            <Appliance onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
          {page === "profile" && (
            <Profile onSwitch={handleSwitch} onLogout={handleLogout} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
